var linkCart=document.querySelector(".buy"),popupCart=document.querySelector(".popup-cart"),closeCart=popupCart.querySelector(".close");linkCart.addEventListener("click",function(e){e.preventDefault(),popupCart.classList.add("modal-show")}),closeCart.addEventListener("click",function(e){e.preventDefault(),popupCart.classList.remove("modal-show")}),window.addEventListener("keydown",function(e){27===e.keyCode&&(e.preventDefault(),popupCart.classList.contains("modal-show")&&popupCart.classList.remove("modal-show"))});